const notificar=(n,t)=>{if(t){switch(n){case"sucesso":n="success";break;case"erro":n="danger";break;case"aviso":n="warning";break;default:n="info"}var i=$(`<div class="alert alert-${n} alert-dismissible"><a href="#" class="close" data-dismiss="alert" aria-label="close">&times;</a>${t}</div>`).hide();$("body").prepend(i);i.fadeIn("short");setTimeout(()=>i.fadeOut("short",()=>i.remove()),5e3)}};$(document).ready(()=>{$.extend($.fn.dataTable.defaults,{bSearching:!1,bSort:!1,bServerSide:!0,sAjaxDataProp:"data"}),$.ajax({url:"/js/datatables-pt-br.json",success:n=>$.extend($.fn.dataTable.defaults,{oLanguage:n})})});